    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
       int open = jFileChooser1.showOpenDialog(this);
       if (open == jFileChooser1.APPROVE_OPTION) {
        File file = jFileChooser1.getSelectedFile();
        try {
        // Read the original image
        BufferedImage originalImage = ImageIO.read(file);

        // Resize the image to 250x250 pixels using getScaledInstance
        // Use Image.SCALE_SMOOTH for better quality
        Image resizedImage = originalImage.getScaledInstance(180, 180, Image.SCALE_SMOOTH);

        // Create an ImageIcon from the resized image and set it to your JLabel
        jLabel4.setIcon(new ImageIcon(resizedImage));

    } catch (IOException e) {
        e.printStackTrace();
    }
       } 
    }//GEN-LAST:event_jButton8ActionPerformed

